#include "../includes/minishell.h"

static void clear_looped_values(void);

int main(int argc, char** argv, char** envp)
{
    (void)argv;
    if(argc != 1)
        return (printf("Wrong Input. Enter ./minishell\n"));
    if(init(envp) == TRUE)
    {
        //ignore_signal_for_shell();
        while (1)
        {
            prompt();
            if (evaluate() == 1)
                break;
            clear_looped_values();
        }
    }
    //close_program();
    return (0);
}

/* Clear all values generated by each line execution. */
static void clear_looped_values(void)
{
    if (mini()->core->rl_returned)
        free(mini()->core->rl_returned);
    if (mini()->core->prompt)
        free(mini()->core->prompt);
    free_list(&mini()->arg_list);
}

/* Clears list if existent. */
void	free_list(t_list **lst)
{
	t_list	*temp;

	if (lst == NULL || *lst == NULL)
	{
        printf("No list to free.\n");
        return ;
    }
	while (*lst)
	{
		temp = (*lst)-> next;
		free(*lst);
		*lst = temp;
	}
	lst = NULL;
}

shell_t *mini(void)
{
    static shell_t mini;

    return (&mini);
}
